import React, { useState } from 'react'
import { useDispatch, useSelector } from "react-redux";
import { incrementByAmount } from "../redux/counter";

function ToKelvin() {
    const { count } = useSelector((state) => state.counter);
    const dispatch = useDispatch();

    const [celcius, setCelcius] = useState(0);

    return (
        <div>
            <h1>Current Number : {count} Kelvin</h1>
            <br />
            <input type='text' value={celcius} onChange={(event) => { setCelcius(event.target.value) }} />
            <br />
            <button onClick={() => dispatch(incrementByAmount(celcius))}>tempt</button>
        </div>
    )
}

export default ToKelvin
