import { createSlice } from '@reduxjs/toolkit'

export const counterSlice = createSlice({
    name: 'toFahrenheit',
    initialState: {
        tempt: 0
    },
    reducers: {
        handleInput: (state, action) => {
            state.tempt += action.payload
        },
        convertToFahrenheit: (state) => {
            state.tempt = (state.tempt * 1.8) + 32
        }
    }
})

export const { handleInput, convertToFahrenheit } = counterSlice.actions

export default counterSlice.reducer
