import { createSlice } from '@reduxjs/toolkit'

export const fahrenheitSlice = createSlice({
    name: 'toFahrenheit',
    initialState: {
        tempt: 0
    },
    reducers: {
        handleInput: (state, action) => {
            state.tempt += (action.payload * 1.8) + 32
        },
        convertToFahrenheit: (state) => {
            state.tempt = (state.tempt * 1.8) + 32
        }
    }
})

export const { handleInput, convertToFahrenheit } = fahrenheitSlice.actions

export default fahrenheitSlice.reducer
